&#34;&gt;	ctx.a.restore();&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;	positions[i] = tx;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;	positions[iy] = ty;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;	velocities[i] = vx;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;	velocities[iy] = vy;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;	lifeSpans[i] = l;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;	(checkBounds(x, y) || l &amp;gt; ttl) &amp;amp;&amp;amp; initParticle(i);&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&amp;#125;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;function checkBounds(x, y) &amp;#123;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;	return (&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;		x &amp;gt; canvas.a.width ||&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;		x &amp;lt; 0 ||&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;		y &amp;gt; canvas.a.height ||&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;		y &amp;lt; 0);&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&amp;#125;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;function createCanvas() &amp;#123;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;	canvas = &amp;#123;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;		a: document.createElement(&amp;quot;canvas&amp;quot;),&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;		b: document.createElement(&amp;quot;canvas&amp;quot;) &amp;#125;;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;	canvas.b.style = &amp;quot;\n\t\tposition: absolute;\n\t\ttop: 0